<html>
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width">
  <title>Lab7 Eric Tapia</title>
<style id="jsbin-css">
body{
  background-color:grey;
}

form{
  background-color:white;
  height:26em;
  width:16em;
}
fieldset{
  background-color:white;
  margin-left:1em;
  width:12em;
  height:12em;
}
div{
  margin-left:1em
}
legend{
  color:#D00D2D;
  font-size:20px
}
</style>
</head>
<body>
<form   id="form">
    <fieldset >
      <legend>Add an Employee</legend>  
      
      First Name<br><input type="text" id="firstName"><br>
      Last Name<br><input type="text" id="lastName"><br>
      
      Department<br>
        <select id="selections">
          <option value="Engineering">Engineering</option>
          <option value="Computer science">Computer Science</option>
          <option value="Physics">Physics</option>
        </select>
        <br><br>    
      
      <input type="submit" value="Submit &#8594" id="submit">
    </fieldset> 
    
    <div id="employeeAdd">
      <h3>Employee Added</h3>
      Name:<label id="name"> </label><br>
      Department:<label id="dept"> </label><br>
      Employee Id:<label id="eId"> </label><br>
      Hire Date:<label id="date"> </label><br>
      Total Employees:<label id="total"> </label><br>    
    </div>
    
  </form>
<script id="jsbin-javascript">
var employees = [];

function employee(fname,lname,dept,date){
  this.fname = fname;
  this.lname = lname;
  this.dept = dept;
  this.date = date;
  this.eId = randID();
}

function addEmployee(){
  var fname = document.getElementById("firstName").value;
  var lname = document.getElementById("lastName").value;
  var date = dayStr()+" "+ monthStr()+" "+dayYearStr();
  
  //var dropMenu = document.getElementById("selections").value;
  //var dept = dropMenu.options[dropMenu.selectedIndex].value;
  
  var dropDown = document.getElementById("selections");
  var dept = dropDown.options[dropDown.selectedIndex].value; 
  
  var empObject = new employee(fname,lname,dept,date);
  employees.push(empObject);

  output(empObject);
  
  console.log(fname);
  console.log(lname);
  console.log(dept);
  console.log(date);
  return false;
}

function output(employee){  
  document.getElementById("name").innerHTML = employee.lname+ " , " +employee.fname;
  
  document.getElementById("dept").innerHTML = employee.dept;
 
  document.getElementById("eId").innerHTML = employee.eId;
  document.getElementById("date").innerHTML = employee.date;
  document.getElementById("total").innerHTML = employees.length;
  
  document.getElementById("employeeAdd");
}
function randID() {
  var randId = (''+Math.random()).substring(2,10);
  return randId;
}
function dayStr(){
  switch (new Date().getDay()) {
    case 0:
        day = "Sun";
        break;
    case 1:
        day = "Mon";
        break;
    case 2:
        day = "Tue";
        break;
    case 3:
        day = "Wed";
        break;
    case 4:
        day = "Thu";
        break;
    case 5:
        day = "Fri";
        break;
    case 6:
        day = "Sat";
        break;
  }
  return day;
}
function monthStr(){
  switch (new Date().getMonth()){
    case 0:
      month = "Jan";
      break;
    case 1:
      month = "Feb";
      break;
    case 2:
      month = "Mar";
      break;
    case 3:
      month = "Apr";
      break;
    case 4:
      month = "May";
      break;
    case 5:
      month = "Jun";
      break;
    case 6:
      month = "Jul";
      break;
    case 7:
      month = "Aug";
      break;
    case 8:
      month = "Sep";
      break;
    case 9:
      month = "Oct";
      break;
    case 10:
      month = "Nov";
      break;
    case 11:
      month = "Dec";
      break;
  }
  return month;
}
function dayYearStr(){
  var date = new Date();
  return ('0' + date.getDate()).slice(-2)+' '+date.getFullYear() ;
}
function init() {
	document.getElementById("form").onsubmit = addEmployee;
} 
window.onload = init;
</script>
